mapper.select:
	   select ,u.c_username,r.c_roleName,a.c_createDate,a.c_updateDate
	   from sys_user_role a
	   left join sys_user u
	   on u.id = a.n_userId
	   left join sys_role r
	   on r.id = a.n_roleId
	   where a.n_deleted = 0
	    and n_userId = #{ur.n_userId}
	    and n_roleId = #{ur.n_roleId}
	   	and substr(c_createDate,1,10) = #{ur.c_createDate}
   	     and substr(c_updateDate,1,10) = #{ur. c_updateDate}
   	   order by id limit #{page.index},#{page.rows}

	   select count(id)
	   from sys_user_role
	   where 1=1
	    and n_userId = #{ur.n_userId}
	    and n_roleId = #{ur.n_roleId}
	   	and substr(c_createDate,1,10) = #{ur.c_createDate}
   	     and substr(c_updateDate,1,10) = #{ur. c_updateDate}
